import React from 'react';
import s from './ProfileInfo.module.css';
import Preloader from "../../common/Preloader/Preloader";
import ProfileStatusHooks from "./ProfileStatusHooks";
import usersPhoto from "../../Users/Photo/usersPhoto.png";

const ProfileInfo = ({profile, status, updateStatus, isOwner, savePhoto}) => {
    if (!profile) {
        return <Preloader/>
    }

    const myPhotoSelected = (e) => {
        if (e.target.files.length) {
            savePhoto(e.target.files[0])
        }
    }
    return (
        <div>
            <div className={s.descriptionBlock}>
                <img src={profile.photos.large || usersPhoto}
                     className={s.myPhoto}/>
                <div>
                    {isOwner &&
                    <input type={'file'} onChange={myPhotoSelected}/>}

                    <ProfileStatusHooks status={status}
                                        updateStatus={updateStatus}/>
                    {editMode ? <ProfileDataForm profile = {profile}/> : <ProfileData profile = {profile}/>}
                    <ProfileData profile={profile}/>
                </div>
            </div>
        </div>
    )
}
const ProfileData = ({profile}) => {
    return (
        <div>
            <div>
                <b>Full Name:</b> {profile.fullName}
            </div>
            <div>
                <b>Looking for a
                    job:</b>{profile.lookingForAJob ? 'yes' : 'no'}
            </div>
            {
                profile.lookingForAJob &&
                <div>
                    <b>My
                        skills:</b>{profile.lookingForAJobDescription}
                </div>
            }
            <div>
                <b>About me:</b> {profile.aboutMe}
            </div>
            <div>
                <b>Contacts:</b> {Object.keys(profile.contacts).map(key => {
                return <Contacts contactTitle={key}
                                 contactValue={profile.contacts[key]}
                />
            })}
            </div>
        </div>
    )
}
const ProfileDataForm = ({profile}) => {
    return (
        <div>
            <div>
                <b>Full Name:</b> {profile.fullName}
            </div>
            <div>
                <b>Looking for a
                    job:</b>{profile.lookingForAJob ? 'yes' : 'no'}
            </div>
            {
                profile.lookingForAJob &&
                <div>
                    <b>My
                        skills:</b>{profile.lookingForAJobDescription}
                </div>
            }
            <div>
                <b>About me:</b> {profile.aboutMe}
            </div>
            <div>
                <b>Contacts:</b> {Object.keys(profile.contacts).map(key => {
                return <Contacts contactTitle={key}
                                 contactValue={profile.contacts[key]}
                />
            })}
            </div>
        </div>
    )
}
const Contacts = ({contactTitle, contactValue}) => {
    return <div>
        <b>{contactTitle}</b>:{contactValue}
    </div>
}

export default ProfileInfo;
