    }],


}


const dialogsReducer = (state = initialState , action, props) => {
    switch (action.type) {
        case ADD_MESSAGE:
            let body = action.newMessageBody;
                props.newMessageBody('')
            return{
                ...state,
            messages:[...state.messages,{id: 'user_7', message: body} ]
