import React from 'react';
import s from './ProfileInfo.module.css';
import Preloader from "../../common/Preloader/Preloader";
import ProfileStatusHooks from "./ProfileStatusHooks";
import usersPhoto from "../../Users/Photo/usersPhoto.png";

const ProfileInfo = ({profile, status, updateStatus, isOwner, savePhoto}) => {
    if (!profile) {
        return <Preloader/>
    }

    const myPhotoSelected = (e) => {
        if (e.target.files.length) {
            savePhoto(e.target.files[0])
        }
    }
    return (
        <div>
            <div>
                <img
                    src='https://cdn.pixabay.com/photo/2018/01/14/23/12/nature-3082832_960_720.jpg'/>
            </div>
            <div className={s.descriptionBlock}>

                <img src={profile.photos.large || usersPhoto}
                     className={s.myPhoto}/>
                <div>
                    {isOwner &&
                    <input type={'file'} onChange={myPhotoSelected}/>}

                    <ProfileStatusHooks status={status}
                                        updateStatus={updateStatus}/>
                </div>
                <div>
                    {profile.fullName}
                </div>
                <div>
                    {profile.contacts.facebook}
                </div>
                <div>
                    {profile.aboutMe}
                </div>
                <div>
                    {profile.contacts.instagram}
                </div>
            </div>

        </div>
    )
}
export default ProfileInfo;
