import React, {useState} from 'react';


const ProfileStatusHooks = (props) => {

    let [edtitMode, setEdtitMode] =  useState(false)

const activateMode =  () => {
    this.setState({
        editMode: true
    })
}
    return (
        <div>
            {!edtitMode &&
            <div>
                <span onDoubleClick={this.activateEditMode}> {props.status || '-----'}js </span>
            </div>
            }
            {edtitMode &&
            <div>
                <input  autoFocus={true}
                      />
            </div>
            }
        </div>

    )

}


export default ProfileStatusHooks;
